const AvailableHardW = () => {

const[hardwares, sethardware]=useState([]);

useEffect(()=>{
  const fetchHardware = async() =>{
  const response= await fetch('https://http-react1-default-rtdb.firebaseio.com/hardware.json');
    const responseData = await response.json();

    const loadedharware = [];
    for(const key in responseData){
      loadedharware.push({
        id:key,
        name: responseData[key].description,
        description: responseData[key].price,
        price: responseData[key].price,
      });
    }
sethardware();
}
fetchHardware();
},[])

  const HardwareList = hardwares.map((hardware) => (
    <HardwareItem
        id={hardware.id}
      key={hardware.id}
      name={hardware.name}
      description={hardware.description}
      price={hardware.price}
    />
  ));
  return (
    <section className={classes.hardware}>
      <Card>
        <ul>{HardwareList}</ul>
      </Card>
    </section>
  );
};
