import { useEffect, useState } from "react";
import classes from "./AvailableHardW.module.css";
import Card from "../UI/Card";
import HardwareItem from "./HardwareItem/HardwareItem";

const AvailableHardW = () => {

const[hardwares, sethardware]=useState([]);
const[isloading, setIsLoading]=useState(true);


useEffect(()=>{
  const fetchHardware = async() =>{
  const response= await fetch('https://http-react1-default-rtdb.firebaseio.com/hardware.json');
    const responseData = await response.json();

    const loadedharware = [];
    for(const key in responseData){
      loadedharware.push({
        id:key,
        name: responseData[key].name,
        description: responseData[key].description,
        price: responseData[key].price,
      });
    }
sethardware(loadedharware);
setIsLoading(false);
}
fetchHardware();
},[])

 if(isloading){
   return(
     <section>
   <p>Loading....</p></section>
   )}

  const HardwareList = hardwares.map((hardware) => (
    <HardwareItem
        id={hardware.id}
      key={hardware.id}
      name={hardware.name}
      description={hardware.description}
      price={hardware.price}
    />
  ));
  return (
    <section className={classes.hardware}>
      <Card>
        <ul>{HardwareList}</ul>
      </Card>
    </section>
  );
};
