import { useRef, useState } from "react";



const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const Refvariable = useRef();
  const [NameValidheKya, setNameValidheKya]= useState(false);

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    if(enteredName.trim() === 0){
      setNameValidheKya(false);
      return;
    }
    setNameValidheKya(true);

    const Ref1Variable = Refvariable.current.value;
    console.log(enteredName);
    console.log(Ref1Variable);

    setEnteredName("");
  };
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="form-control">
        <label htmlFor="name">Your Name</label>
        <input
          ref={Refvariable}
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          value={enteredName}
        required/>
        {!NameValidheKya && <p className="error-text">Kuch to Daal Name me!!</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
