import { useState } from "react";




const BasicForm = (props) => {

  const[enteredFname, setEnteredFName]=useState('');


  const FnameValidHandler=(event)=>{
    setEnteredFName(event.target.value);
  };
  const FnameInvalid = !FnameValidHandler && enteredFname.trim() === '';




  return (
    <form>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input onChange={FnameValidHandler} type='text' id='name' />
          {FnameInvalid && <p>First Name Invalid</p>}
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
