import { useRef, useState } from "react";



const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const Refvariable = useRef();
  const [NameValidheKya, setNameValidheKya]= useState(false);
  const [FormlaTouchkele, setFormlaTouchkele]=useState(false);

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setFormlaTouchkele(true);

    if(enteredName.trim() === ''){
      setNameValidheKya(false);
      return;
    }
    setNameValidheKya(true);

    const Ref1Variable = Refvariable.current.value;
    console.log(enteredName);
    console.log(Ref1Variable);

    setEnteredName('');
  };

  const NameInvalidhe = !NameValidheKya && FormlaTouchkele;

  const CheckKarAndCSSBadal =  NameInvalidhe
    ? "form-control  invalid"
    : "form-control"; 
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={CheckKarAndCSSBadal}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={Refvariable}
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          value={enteredName}
        />
        {NameInvalidhe && <p className="error-text">Kuch to Daal Name me!!</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
