import {  useState } from "react";
import Uinput from '../hooks/use-input';



const SimpleInput = (props) => {
  const {
    value: enteredName,
    isValid: NameValidahe, 
    NameInvalidhe: nameInputHasError,
    nameInputChangeHandler: nameChangeHandler,
    nameDaalNhitarBlur: nameBlurHandler,
    reset: resetNameInput,
  } = Uinput((value) => value.trim() !== "");


  const [enteredEmail, setEmail]= useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  const EmailValidahe = enteredEmail.includes('@');
  const EmailInvalidahe = !EmailValidahe && enteredEmailTouched;
  let FormValid = false;
    if(NameValidahe && EmailValidahe){
      FormValid=true;
    } 


  const emailInputChangeHandler = (event) => {
    setEmail(event.target.value);

  };

  const emailDaalNhitarBlur = event =>{
    setEnteredEmailTouched(true);
  };

  const formSubmissionHandler = (event) => {
    event.preventDefault();



    if(!NameValidahe && !EmailValidahe){
      return;
    }
    resetNameInput();
    setEmail('');
    setEnteredEmailTouched(false);
  };



  const CheckKarAndCSSBadal =  nameInputHasError
    ? "form-control  invalid"
    : "form-control"; 

  const CheckKarAndCSSBadal1 = EmailInvalidahe
  ? "form-control  invalid"
  : "form-control"; 

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={CheckKarAndCSSBadal}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameChangeHandler}
          onBlur={nameChangeHandler}
          value={enteredName}
        />
        {nameInputHasError && <p className="error-text">Kuch to Daal Name me!!</p>}
      </div>
      <div className={CheckKarAndCSSBadal1}>
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={emailInputChangeHandler}
          onBlur={emailDaalNhitarBlur}
          value={enteredEmail}
        />
        {EmailInvalidahe && <p className="error-text">Kuch to Daal Email me!!</p>}
      </div>
      <div className="form-actions">
        <button disabled={!FormValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
