import {   useState } from "react";



const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState("");
  const [FormlaTouchkele, setFormlaTouchkele]=useState(false);
  const [enteredEmail, setEmail]=useState("");


  const NameValidahe = enteredName.trim() !=='';
  const NameInvalidhe = !NameValidahe && FormlaTouchkele;
  const EmailValidahe = enteredEmail.trim() !=='' && enteredEmail.trim() =='@';
  const EmailInvalidahe = !EmailValidahe && FormlaTouchkele;
  let FormValid = false;
    if(NameValidahe && EmailValidahe){
      FormValid=true;
    } 

  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);

  };

  const nameDaalNhitarBlur = event =>{
     setFormlaTouchkele(true);
  }
  const emailDaalNhitarBlue = event =>{
    setFormlaTouchkele(true);
  }

  const formSubmissionHandler = (event) => {
    event.preventDefault();

    setFormlaTouchkele(true);

    if(!NameValidahe && !EmailValidahe){
      return;
    }
    setEnteredName('');
    setEmail('');
    setFormlaTouchkele(false);
  };



  const CheckKarAndCSSBadal =  NameInvalidhe
    ? "form-control  invalid"
    : "form-control"; 
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={CheckKarAndCSSBadal}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          onChange={nameInputChangeHandler}
          onBlur={nameDaalNhitarBlur}
          value={enteredName}
        />
        {NameInvalidhe && <p className="error-text">Kuch to Daal Name me!!</p>}
      </div>
      <div className={CheckKarAndCSSBadal}>
        <label htmlFor="email">Your Email</label>
        <input
          type="email"
          id="email"
          onChange={nameInputChangeHandler}
          onBlur={nameDaalNhitarBlur}
          value={enteredName}
        />
        {NameInvalidhe && <p className="error-text">Kuch to Daal Name me!!</p>}
      </div>
      <div className="form-actions">
        <button disabled={!FormValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
